package org.fzdu.design.patterns.structure.adapter_interface;

/**
 * 当不希望实现一个接口中的所有方法时，可以创建一个抽象类，实现所有方法，我们写别的类的时候，实现该抽象类即可
 * @author Administrator
 *
 */
public abstract class Adapter implements Sourceable {

	public void method1() {
		// TODO Auto-generated method stub

	}

	public void method2() {
		// TODO Auto-generated method stub

	}

}
